/* eslint-disable no-unused-vars */
import React, { useState, useRef } from "react";
import PropTypes from "prop-types";
import { Input as AntInput, Image } from "antd";
import { CloseOutlined } from "@ant-design/icons";

import { Controller, useWatch } from "react-hook-form";
function FileUpload({
  control,
  inputName,
  validation = {},
  errors,
  inputProps = {},
  defaultValue = "",
  callback = () => {},
}) {
  const [url, setUrl] = useState("");
  const ref = useRef();
  const handleFileChange = (file) => {
    const reader = new FileReader();
    reader.onloadend = () => {
      setUrl(reader.result);
    };
    reader.readAsDataURL(file);
  };
  return (
    <>
      <Controller
        control={control}
        name={inputName}
        rules={validation}
        defaultValue={defaultValue}
        render={({ onChange, value, name }) => {
          return (
            <div className="image-upload">
              <input
                style={{ display: "none" }}
                type="file"
                onChange={(v) => {
                  onChange(v.target.files[0]);
                  handleFileChange(v.target.files[0]);
                  callback(v.target.files[0]);
                }}
                name={name}
                ref={ref}
                accept={"image/*"}
              />
              <AntInput
                {...inputProps}
                type="button"
                className={"button file-button"}
                value="画像を選択してください"
                onClick={() => {
                  ref.current.click();
                }}
              />
              <div className="image-preview-container">
                <span
                  className={"close-container"}
                  onClick={() => {
                    setUrl("");
                    onChange(null);
                  }}
                >
                  <CloseOutlined />
                </span>
                <Image
                  width={120}
                  height={120}
                  src={url || (typeof value === "string" ? value : "")}
                  fallback="data:image/png;base64,"
                />
              </div>
            </div>
          );
        }}
      />
      <small className="invalid-feedback">{errors && errors.message}</small>
    </>
  );
}

FileUpload.propTypes = {
  control: PropTypes.any,
  inputName: PropTypes.string,
  inputProps: PropTypes.object,
  validation: PropTypes.object,
  errors: PropTypes.any,
  getValues: PropTypes.any,
  defaultValue: PropTypes.string,
  callback: PropTypes.func,
};

export default FileUpload;
